import React from 'react'

class Progress extends React.Component {
    constructor() {
        super()

    }

    render() {
        let {totalRaised, goal} = this.props

        return(<>
            <h2 className='mb-4'>Raised $ {totalRaised} of <span className='text-muted'>$ {goal}</span></h2>
            <div className="progress">
                <div className="progress-bar bg-success" role="progressbar" style={{width: `${totalRaised/goal*100}%`,}} aria-valuenow={2}
                    aria-valuemin="0" aria-valuemax="100">{99}%</div>
            </div>
            <hr />
        </>)
    }
}

export default Progress
