import React from 'react';
import './App.css';
import Donations from './Components/Donations';
import Progress from './Components/Progress';
import Form from './Components/Form';

class App extends React.Component {
  constructor() {
    super()
    this.state = {
      donations: [
        {
            donor: 'John', 
            amount: 5000, 
            message: "Let's all give to this great cause!",
        },
        {
            donor: 'Jane', 
            amount: 66, 
            message: "Tis the season to be donating",
        },
        {
            donor: 'Jack Ma', 
            amount: 34, 
            message: "Arribaba ee bettrer dan amaszhon",
        },
      ],
      currentFormUser: {
        donor: '',
        amount: 5,
        message: '',
      },
      totalRaised: () => this.state.donations.reduce((t, d) => t += d.amount, 0),
      goal: 1000,
    }
  }



  render() {
    let {donations, totalRaised, currentFormUser, goal} = this.state


    return (
      <div className="App">
        <div className='container'>
          <div className='row'>
            <Donations donations={donations}/>
            <div className='col-8'>
              <Progress 
                totalRaised={totalRaised()}
                goal={goal}
              />
              <Form 
                sliderValue={totalRaised()}
                // name={name}
                // message={message}
                // submitForm={submitForm}
                // handleName={handleName}
                // handleMessage={handleMessage}
                // handleSlider={handleSlider}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
